mapper.select:
SELECT *
FROM MEMBER
WHERE ME_ID=#{userId}
AND ME_PW=#{userPwd}
AND ME_STATUS='Y'

SELECT COUNT(*) FROM MEMBER
WHERE USERID = #{userId}
AND USERPASS = #{userPass}

mapper.update:
update member
set me_con_num = con_seq.currval
where me_id = #{userId}

update member
me_recent1 = #{recent1}, me_recent2 = #{recent2}, me_recent3 = #{recent3}, me_recent4 = #{recent4}, me_recent5 = #{recent5},
where me_id = #{userId}

mapper.select:
select count(*)
from member
where me_nick = #{nickName}

mapper.update:
update member
set me_pw = #{userPwd}, me_name = #{userName}, me_nick = #{nickName}, me_phone = #{phone}, me_email = #{email},
	me_intro = #{intro}

	, me_acount = #{account}	


	, me_bank = #{bank}	

where me_id = #{userId}

update request
set re_grade = #{reGrade}
where re_num = #{reNum} and re_id = #{reId}

mapper.select:
select me_id
from member
where me_nick = #{reId}

select ME_EASY
from member
where me_id = ${userId}

mapper.update:
update member
set me_easy = 'Y'
where me_id = ${userId}
