mapper.select:
SELECT *
FROM MEMBER
WHERE ME_ID=#{userId}
AND ME_PW=#{userPwd}
AND ME_STATUS='Y'

mapper.update:
update member
set me_con_num = con_seq.currval
where me_id = #{userId}

update member
me_recent1 = #{recent1}, me_recent2 = #{recent2}, me_recent3 = #{recent3}, me_recent4 = #{recent4}, me_recent5 = #{recent5},
where me_id = #{userId}
